mapper.select:

SELECT * FROM (SELECT ROWNUM NUM , S.* FROM (SELECT a.*,  b.RENAME_FILENAME FROM SALES_POST a LEFT OUTER JOIN IMAGES b ON a.POST_NO = b.POST_NO 


	AND POST_TITLE LIKE '%'||#{post_title}||'%'


	AND POST_ADDRESS LIKE '%'||#{post_address}||'%'



	AND POST_CODE = #{category} 		


	AND POST_YN = #{onsale}



ORDER BY a.POST_NO DESC)S) WHERE NUM BETWEEN 1 AND 8



SELECT * FROM (SELECT ROWNUM NUM , S.* FROM (SELECT a.*,  b.RENAME_FILENAME FROM SALES_POST a LEFT OUTER JOIN IMAGES b ON a.POST_NO = b.POST_NO 


	AND POST_TITLE LIKE '%'||#{post_title}||'%'



	AND POST_CODE = #{category} 		


	AND POST_YN = #{onsale}



ORDER BY a.POST_NO DESC)S) WHERE NUM BETWEEN 8+(4*#{more}) AND 11+(4*#{more})

mapper.insert:
{ CALL
BEGIN
	INSERT INTO SALES_POST(POST_NO, POST_TITLE, POST_PRICE, POST_CONTENTS, POST_ADDRESS, POST_TAGS, ID, POST_CODE) VALUES(SALES_POST_SEQ.NEXTVAL, #{post_title}, #{post_price}, #{post_contents}, #{post_address}, #{post_tags}, #{id}, #{post_code});
	INSERT INTO IMAGES (ORIGINAL_FILENAME, RENAME_FILENAME, FILE_PATH, POST_NO) VALUES(#{original_filename},#{rename_filename},#{file_path}, SALES_POST_SEQ.CURRVAL);
END
}
